package com.tw.viewpagerdemo

import android.os.Bundle
import androidx.appcompat.app.AppCompatActivity
import com.tw.viewpagerdemo.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {

    lateinit var binding: ActivityMainBinding
    val TAG:String = this.javaClass.simpleName

    val list: MutableList<String> = mutableListOf()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        getList(list)
    }

    private fun getList(list: MutableList<String>) {
        list.add("data:image/jpeg;base64,")
        list.add("data:image/jpeg;base64,")
        list.add("data:image/jpeg;base64,")

        binding.viewBanner.startAutoScroll()
        binding.viewBanner.interval = 3L*1000
        binding.viewBanner.isCycle = true
        binding.viewBanner.isStopScrollWhenTouch = true
        binding.tabBanner.setupWithViewPager(binding.viewBanner, true)

        binding.viewBanner.adapter = BannerAdapter(
            this@MainActivity,
            list as MutableList<String>,
        )
    }


}